import React, { useState } from "react";

export default function Accinput(){
    const [accomodation,setAccomodation]=useState({
        name:"",
        category:"",
        type:"",
        tv:true,
        safe:true,
        fridge:true,
        ac:true,
        internet:true,
        location:{
            city:"",
            country:"",
            continent:""
        }
    });

    const updateName=(e)=>{
        setAccomodation(previousData=>{
            return{...previousData,name:(e.target.value)}
        })
    }
    const updateCategory=(e)=>{
        setAccomodation(previousData=>{
            return{...previousData,category:(e.target.value)}
        })
    }
    const updateType=(e)=>{
        setAccomodation(previousData=>{
            return{...previousData,type:(e.target.value)}
        })
    }
    const updateTv=(e)=>{
        setAccomodation(previousData=>{
            return{...previousData,tv:(e.target.value)}
        })
    }
    const updateSafe=(e)=>{
        setAccomodation(previousData=>{
            return{...previousData,safe:(e.target.value)}
        })
    }
    const updateFrifge=(e)=>{
        setAccomodation(previousData=>{
            return{...previousData,fridge:(e.target.value)}
        })
    }
    const updateAc=(e)=>{
        setAccomodation(previousData=>{
            return{...previousData,ac:(e.target.value)}
        })
    }
    const updateInternet=(e)=>{
        setAccomodation(previousData=>{
            return{...previousData,internet:(e.target.value)}
        })
    }
    const updateCity=(e)=>{
        setAccomodation(previousData=>({...previousData,location:{...previousData,city:(e.target.value)}}));
    }
    const updateCountry=(e)=>{
        setAccomodation(previousData=>({...previousData,location:{...previousData,country:(e.target.value)}}));
    }
    const updateContinent=(e)=>{
        setAccomodation(previousData=>({...previousData,location:{...previousData,continent:(e.target.value)}}));
    }
    return(
        <div className="row my-2 p-2" style={{borderTop:"solid"}}>
            <div className="col-md-5">
                <label className="form-label my-1" htmlFor="name">Naziv aranzmana:</label>
                <input className="form-control" 
                    type="text" 
                    id="name" 
                    name="name"
                    value={accomodation.name}
                    onChange={updateName}
                    style={{borderRadius:'20px'}}></input>
        </div>
        </div>
    );
}
