import React, { useState } from "react";

export default function Updatearr(){
    const [search,setSearch]=useState("");
    const [arr,setArr] = useState([]);
    const [page, setPage] = useState(0);
    const [size, setSize] = useState(50);
    const [totalPages,setTotalPages]=useState(0);

    const doOnRender=(data)=>{
        setArr(data.content);
        setTotalPages(data.totalPages);
    };

    const getArr=()=>{
        var x = `http://localhost:8080/arrangements/all?page=${page}&size=${size}`;
        axios.get(x)
            .then((response) => response.data)
            .then((data) => (
                doOnRender(data)
            ));
    };

    const getSearch=()=>{
        var x = `http://localhost:8080/arrangements/get?`+
            (search.name?"name="+search.name+"&":"") +
            (search.city?"city="+search.city+"&":"") +
            (search.country?"country="+search.country+"&":"") +
            (search.continent?"continent="+search.continent+"&":"") +
            (search.transportation?"transportation="+search.transportation+"&":"") +
            (search.startdate?"startDate="+search.startdate+"&":"") +
            (search.enddate?"endDate="+search.enddate+"&":"") +
            `page=${page}&size=${size}`;
        axios.get(x)
            .then((response) => response.data)
            .then((data) => (
                doOnRender(data)
            ));
    };

    const onSearch=()=>{
        getSearch()
    }

    React.useEffect(() => {
        /*if (checkSearch()){*/
            getSearch()
        /*}else{
            getArr()
        }*/
    }, [page]);

    const firstPage=()=>{
        setPage(0)
    }
    const lastPage=()=>{
        setPage(totalPages-1)

    }
    const prevPage=()=>{
        setPage(page-1)

    }
    const nextPage=()=>{
        setPage(page+1)
    }

    const cancelSearch=()=>{
        setSearch("")
        getArr();
    }

    return(
        <div className="rowj">
            <form className="row justify-content-end">
                <div className="col-md-4">
                    <label htmlFor="search" className="form-label text-">Pretrazi po nazivu:</label>
                    <input className="form-control" 
                        type="text" 
                        id="search" 
                        name="search"
                        value={search}
                        onChange={(e)=>setSearch(e.target.value)}
                        style={{borderRadius:'20px'}}
                        ></input>
                </div>
            </form>

        </div>
    );
};
