import React, { useState } from "react";

export default function Addarrangement(){
    const [arrangement,setArrangement]=useState({
        name:"",
        price:0,
        transportation:"",
        status:"",
        remark:"",
        accomodations:[],
        programs:[]
    })

    const updateName=(e)=>{
        setArrangement(previousData=>{
            return{...previousData,name:(e.target.value)}
        })
    }
    const updatPrice=(e)=>{
        setArrangement(previousData=>{
            return{...previousData,price:(e.target.value)}
        })
    }
    const updateTransportation=(e)=>{
        setArrangement(previousData=>{
            return{...previousData,transportation:(e.target.value)}
        })
    }
    const updateStatus=(e)=>{
        setArrangement(previousData=>{
            return{...previousData,status:(e.target.value)}
        })
    }
    const updateRemark=(e)=>{
        setArrangement(previousData=>{
            return{...previousData,remark:(e.target.value)}
        })
    }
    /*const updateAccomodations=(e)=>{
        setArrangement(previousData=>{
            return{...previousData,accomodations:(oldArray => [...oldArray,newElement])}
        })
    }
    const updatePrograms=(e)=>{
        setArrangement(previousData=>{
            return{...previousData,programs:(oldArray => [...oldArray,newElement])}
        })
    }*/
    return(
        <div className="conteiner">
            <form className="row ">
                <div className="col-md-5">
                    <label className="form-label" htmlFor="name">Naziv aranzmana:</label>
                    <input className="form-control" 
                        type="text" 
                        id="name" 
                        name="name"
                        value={arrangement.name}
                        onChange={updateName}
                        style={{borderRadius:'20px'}}></input>
                    <label htmlFor="price" className="form-label">Cena u evrima:</label>
                    <input 
                        type="number" 
                        className="form-control"  
                        placeholder="Unesite cenu aranzmana"  
                        name="price"
                        value={arrangement.price}
                        onChange={updatPrice} 
                        style={{borderRadius:'20px',width:'120px'}}
                        required></input>
                    <label htmlFor="transportation" className="form-label">Tip prevoza:</label>
                    <br></br>
                    <select value={arrangement.transportation} name="transportation" onChange={updateTransportation} style={{borderRadius:'20px',height:"30px"}} required>
                        <option value="Avion">Avion</option>
                        <option value="Autobus">Autobus</option>
                        <option value="Krstarenje">Krstarenje</option>
                        <option value="Samostalni prevoz">Samostalni prevoz</option>
                        <option value="Voz">Voz</option>
                        <option selected value="">...</option>
                    </select>
                </div>
                <div className="col-md-5">
                    <label htmlFor="remark" className="form-label">Komentar:</label>
                    <br></br>
                    <textarea value={arrangement.remark} name="remark" onChange={updateRemark} rows={5} cols={10} style={{borderRadius:'20px',height:"200px",width:"400px"}} required/>
                </div>
            </form>
        </div>
    );
}
